Action.Code:
(L:CheckListThrottle_NextCheckTime) (E:SIMULATION TIME, seconds) < if{
  (L:IsFirstRPMAdjustment) if{
    0.10 (>B:ENGINE_Throttle_1_Set)
    0 (>L:IsFirstRPMAdjustment)
    (E:SIMULATION TIME, seconds) 15.0 + (>L:CheckListThrottle_NextCheckTime)
  } els{
    (A:GENERAL ENG RPM:1,rpm) sp1
    l1 (L:ChecklistThrottle_LastRPM) - (>L:ChecklistThrottle_DeltaRPM)
    (L:ChecklistThrottle_DeltaRPM) abs 5 <
    l1 1200 - abs (L:ChecklistThrottle_LastDeltaToTargetRPM) abs >
    or if{
      l1 1200 < if{
        0.5 (>B:ENGINE_Throttle_1_Inc)
      } els{
        l1 1200 > if{
          0.5 (>B:ENGINE_Throttle_1_Dec)
        }
      }
    }
    l1 (>L:ChecklistThrottle_LastRPM)
    l1 1200 - (>L:ChecklistThrottle_LastDeltaToTargetRPM)
    (E:SIMULATION TIME, seconds) 1.0 + (>L:CheckListThrottle_NextCheckTime)
  }
}

